mapper.select:
SELECT TT1.* FROM ( SELECT ROWNUM RNUM,T1.*,T2.* FROM(
SELECT C.SALE_CNT,C.PRO_NAME , C.PRO_CONTENT , C.PRO_SEQ , C.PRO_PRICE , C.ITEM , D.STORED_NAME , COUNT(E.PRO_SEQ) AS REVIEW_CNT FROM ( SELECT COUNT(B.PRO_SEQ) AS SALE_CNT , A.PRO_NAME , A.PRO_CONTENT , A.PRO_SEQ , A.PRO_PRICE , A.ITEM FROM PRODUCT A LEFT JOIN SALES B ON A.PRO_SEQ=B.PRO_SEQ WHERE A.ITEM= #{ITEM}
GROUP BY B.PRO_SEQ ,A.PRO_NAME ,A.PRO_CONTENT ,A.PRO_SEQ ,A.PRO_PRICE ,A.ITEM )C LEFT JOIN PHOTO D ON C.PRO_SEQ = D.PRO_SEQ LEFT JOIN REVIEW E ON C.PRO_SEQ = E.PRO_SEQ WHERE D.STORED_NAME LIKE 'MAIN%' GROUP BY C.SALE_CNT , C.PRO_NAME , C.PRO_CONTENT , C.PRO_SEQ , C.PRO_PRICE , C.ITEM , D.STORED_NAME , E.PRO_SEQ ORDER BY ${ORDER}
) T1 NATURAL JOIN(
SELECT COUNT(*) TOT_CNT FROM( SELECT COUNT(A.PRO_SEQ) FROM PRODUCT A LEFT JOIN SALES B ON A.PRO_SEQ=B.PRO_SEQ
GROUP BY A.PRO_SEQ )
) T2 ) TT1 WHERE RNUM BETWEEN (#{PAGE_SIZE} * (#{PAGE_NUM}-1)+1) and ((#{PAGE_SIZE}*(#{PAGE_NUM}-1))+#{PAGE_SIZE})
